import React, { Component } from 'react';
import './App.css';

import Header from './components/header/Header'
import Instruction from './components/instructions/Instruction'

class App extends Component{


  constructor(){
    super()

    //state for characters value//
    this.state = {
      headerName: "Hello I am header coming from state",
      hide:false
    }


}
  hideInstruction(){
    this.setState({hide:true});
  }


  render(){
    return(
    <div className="App">
      <Header headerName = "Clicky Game"/>
      <button onClick={this.hideInstruction}>Click me to hide Instruction</button>
      {this.state.hide?null:<Instruction/>}
    </div>
    )};
}

export default App;
